import React, { Component } from 'react';
import { ImageBackground, StyleSheet, Text, View } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { TextInput } from 'react-native-gesture-handler';




export default class Login extends Component{


    constructor(props){
        super(props)
        this.state={
            email:'',
            password:''

        }
    }


    render(){
        return(
            <View style={{flex:1,
                backgroundColor:'#ffffff',  
               alignItems: 'center',
                justifyContent: 'center',
                alignContent:"center" }}>
                   <LinearGradient
                colors={['rgba(0.8,0.2,0.2,0.3)', 'transparent']}
                style={styles.background}
              />
               <ImageBackground source={require('./images/lms-learning-management-system-vector_116137-2872-removebg.png')}style={styles.imageview}
        ></ImageBackground>
                
                <View style={{
                    backgroundColor:'#062E4A',
                    paddingtop: 16,
                    height: 46,
                    width: 400,
                    color:'#fff',
                    fontweight: 'normal',
                    borderRadius:5,
                    alignSelf:'center',
                    alignContent:'center',
                    alignItems:'center',
                    
                }}>
                    <Text style={{fontFamily:'YanoneKaffeesatz',fontSize:26,color:"#ffffff",alignContent:'center',alignSelf:'center',alignItems:'center',marginTop:5}} >Login</Text>
                </View>
                <View style={{
                    backgroundColor:'#EDFFFF',
                    height:200,
                    width:400,
                    borderRadius:5,
                    alignContent:'center',
                    alignItems:'center',
                    alignSelf:'center'
                }}>
                    <View style={styles.inputView}>
                        <TextInput style={
                            styles.TextInput
                        }
                        placeholder="ID / Email"
                        placeholderTextColor="#003f5c"
                        onChangeText={(email) => this.setState({email})}
                        
                        />


                    </View>
                    <View style={styles.inputView}>
                        <TextInput style={
                            styles.TextInput
                        }
                        placeholder="Password"
                        placeholderTextColor="#003f5c"
                        onChangeText={(password) => this.setState({password})}
                        
                        />

                    </View>


                </View>
            </View>
        );

    }
}


const styles=StyleSheet.create({
    background: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        height: 350,
      },
      imageview:{
        width: 350,
        height:300,
        marginBottom:25,
        alignContent:'center',
      },
      inputView:{
        marginBottom:20,
  
        alignItems:"center",
        alignContent:"center",
        justifyContent:"space-evenly",
        backgroundColor: "#ffffff",
        borderRadius: 5,
        width: "80%",
        height: 55,
      },
      TextInput:{
        alignContent:"center",
        justifyContent:"center",
        alignItems:"center",
        fontFamily:"monospace",
        width:250,
        
  
      }

})
