import React, { Component } from 'react';
import { ImageBackground, StyleSheet,Image, Text, TouchableOpacity, View } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { TextInput } from 'react-native-gesture-handler';
import Checkbox from 'expo-checkbox';





export default class Login extends Component{


    constructor(props){
        super(props)
        this.state={
            email:'',
            password:'',
            isSelected:false

        };
        this.handleCheckboxPress = this.handleCheckboxPress.bind(this);
    }

    handleCheckboxPress(){
      const { isSelected} = this.state;
      this.setState({isSelected: !isSelected});
    }
    
    onpresslogin(){
      this.props.navigation.navigate('Main');
    }
    onpressReset(){
      this.props.navigation.navigate('Forgotpass');
    }



    render(){
      const { isSelected } = this.state;
        return(
          <View style={{
            flexDirection:"row",
            height:"100%",
            width:"100%"
          }}>
            <View
            style={{
              width:"50%",
              height:"100%"
            }}
            >
              <Image source={require('./images/311381381_982955666431550_812384766897590576_n_cleanup.jpg')}
              style={{
                width:"100%",
                height:"100%"
              }}
              resizeMode="stretch"
              > 

              </Image>
            </View>
            <View style={{flexDirection:'column',alignContent:"center",justifyContent:"center",alignSelf:"center",width:"50%",height:"100%",backgroundColor:"#ffffff",alignItems:"center"}}>
              <Image source={require('./images/WhatsApp_Image_2023-02-28_at_5.37.06_PM-removebg-preview.png')}
                    style={{height:350,
                    width:350,
                    marginLeft:10
                    }}>
                
              </Image>
       <Text style={{fontFamily:'Roboto',fontSize:50,fontWeight:'bold',color:'black',paddingTop:10,paddingLeft:40,alignSelf:'center',fontStyle:"normal"}}>EMAAN INSTITUTE</Text> 
       <Text style={{fontFamily:'Roboto',fontSize:29,fontWeight:'bold',color:'#880606',paddingTop:22,paddingLeft:40,alignItems:'center',textAlign:'center'}}>Learning Management System</Text> 
       <View style={{
        

                    backgroundColor:'#ffffff',
                    marginTop:40,
                    height:450,
                    marginLeft:40,
                    width:550,
                    alignContent:'center',
                    alignItems:'center',
                    alignSelf:'center'
                 }}>
   
        <Text style={{justifyContent:'center',alignContent:'center',alignSelf:'center',paddingTop:10, fontFamily:"YanoneKaffeesatz",color:'#ffffff', fontSize:22,fontWeight:200}}>Reset Password</Text>
                    <View style={styles.inputViewB}>
                        <TextInput style={
                            styles.TextInput
                        }
                        placeholder="Email"
                        placeholderTextColor="#003f5c"
                        onChangeText={(email) => this.setState({email})}
                        
                        />


                    </View>
                    <View style={styles.inputViewB}>
                        <TextInput style={
                            styles.TextInput
                        }
                        placeholder="New Password"
                        placeholderTextColor="#003f5c"
                        secureTextEntry={true}
                        onChangeText={(password) => this.setState({password})}
                    
                        
                        />

                    </View>
                    <View style={styles.inputViewB}>
                        <TextInput style={
                            styles.TextInput
                        }
                        placeholder="Confirm Password"
                        placeholderTextColor="#003f5c"
                        secureTextEntry={true}
                        onChangeText={(confirmpassword) => this.setState({confirmpassword})}
                    
                        
                        />

                    </View>

                    <TouchableOpacity style={{
                                          fontWeight:'bold',
                                          height:40,
                                          width:200,
                                          borderLeftColor:'#FF9FFA',
                                          borderRadius:25,
                                          alignItems:"center",
                                          justifyContent:"center",
                                          alignSelf:"center",
                                          marginTop:40,
                                          backgroundColor: '#ffffff',
                                          shadowRadius:3

                        }}
                        onPress={this.onpresslogin.bind(this)}
                        
                        >Save</TouchableOpacity>


                 </View>               
                 



       



        
        
        </View> 
              </View>

          

        );

    }
}


const styles=StyleSheet.create({
    box: {
        width: '100%',
        height: '100%',
        borderRadius:4
      },
      container: {
        flexDirection:'row',
        borderTopRightRadius:40,
        borderBottomRightRadius:40,
        borderRightWidth:20,
        borderRightColor:'#103a5e',
        justifyContent:'flex-end',
        alignContent:'flex-end',
        alignItems:'flex-end',
        marginLeft:600,
        alignSelf:'flex-end',
        width:250,height:225,
        backgroundColor: '#1a567f',
        borderRadius: 10,
        shadowColor: '#000',
        shadowOffset: {
          width: 0,
          height: 5,
        },
        shadowOpacity: 0.28,
        shadowRadius: 3.84,
        elevation: 8,
        borderStyle:''
      },
      containerB: {
        flexDirection:'row',
        borderTopRightRadius:40,
        borderBottomRightRadius:40,
        borderRightWidth:20,
        borderRightColor:'#103a5e',
        justifyContent:'flex-end',
        alignContent:'flex-end',
        alignItems:'flex-end',
        marginLeft:30,
        alignSelf:'flex-end',
        width:250,height:225,
        backgroundColor: '#1a567f',
        borderRadius: 10,
        shadowColor: '#000',
        shadowOffset: {
          width: 0,
          height: 5,
        },
        shadowOpacity: 0.28,
        shadowRadius: 3.84,
        elevation: 8,
        borderStyle:''
      },
      containerC: {
        flexDirection:'row',
        marginTop:40,
        borderTopRightRadius:40,
        borderBottomRightRadius:40,
        borderRightWidth:20,
        borderRightColor:'#103a5e',
        justifyContent:'flex-end',
        alignContent:'flex-end',
        alignItems:'flex-end',
        marginLeft:30,
        alignSelf:'flex-end',
        width:250,height:225,
        backgroundColor: '#1a567f',
        borderRadius: 10,
        shadowColor: '#000',
        shadowOffset: {
          width: 0,
          height: 5,
        },
        shadowOpacity: 0.28,
        shadowRadius: 3.84,
        elevation: 8,
        borderStyle:''
      },
    background: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        height: 350,
      },
      imageview:{
        width: 450,
        height:400,
        marginBottom:25,
        alignContent:'center',
        justifyContent:'center',
        alignItems:'center',
        alignSelf:'center'
      },
      inputViewA:{
        borderBottomWidth:2,
        borderBottomColor:'#FF595C',
        marginBottom:10,
        marginTop:30,
        alignSelf:'center',
        alignItems:'center',
        alignContent:'center',
        justifyContent:"space-evenly",
        backgroundColor: "#ffffff",
        borderRadius: 28,
        width: "15%",
        height: 55,
      },
      inputViewB:{
        marginTop:40,
        alignSelf:'center',
        alignItems:'center',
        alignContent:'center',
        justifyContent:'center',
        backgroundColor: "#ffffff",
        borderRadius: 4,
        width: "80%",
        height: 55,
      },
      TextInput:{
        alignContent:"center",
        justifyContent:"center",
        alignItems:"center",
        fontFamily:"monospace",
        width:"100%",
        paddingLeft:8,
        height:55
        
        
  
      },
      imageviewbackground:{
        height:1000,
        width:800,

      }

})
