import React, { Component } from 'react';
import { ImageBackground, StyleSheet, Text, View } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';




export default class Login extends Component{


    constructor(props){
        super(props)
        this.state={
            email:'',
            password:''

        }
    }


    render(){
        return(
            <View style={{flex:1,
                backgroundColor:'#ffffff',  
               alignItems: 'center',
                justifyContent: 'center',
                alignContent:"center" }}>
                   <LinearGradient
                colors={['rgba(0.8,0.2,0.2,0.3)', 'transparent']}
                style={styles.background}
              />
               <ImageBackground source={require('./images/lms-learning-management-system-vector_116137-2872-removebg.png')}style={styles.imageview}
        ></ImageBackground>
                
                <View style={{
                    backgroundColor:'#062E4A',
                    paddingtop: 16,
                    height: 46,
                    width: 400,
                    color:'#fff',
                    fontweight: 'normal',
                    borderRadius:5,
                    alignContent:'center',
                    alignItems:'center',
                    marginTop: -25,
                    marginBottom: -25,
                    marginLeft:0,
                    marginRight:25
                }}>
                    <Text style={{fontFamily:'YanoneKaffeesatz',fontSize:26,color:"#ffffff"}} >Sign Up</Text>
                </View>
            </View>
        );

    }
}


const styles=StyleSheet.create({
    background: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        height: 350,
      },
      imageview:{
        width: 250,
        height:200,
        marginBottom:15
      }
})
