import React, { Component } from 'react';
import { ImageBackground, StyleSheet,Image, Text, TouchableOpacity, View } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { TextInput } from 'react-native-gesture-handler';
import Checkbox from 'expo-checkbox';





export default class Login extends Component{


    constructor(props){
        super(props)
        this.state={
            email:'',
            password:''

        }
    }


    render(){
        return(
           <View style={{
            flexDirection:'column',
            flex:1,
           }}>
            <View style={{
                height:'18%',
                width:'100%',
                flexDirection:'row',
                backgroundColor:'#ffffff'
            }}>
                <View>
                    <ImageBackground source={require('./images/WhatsApp_Image_2023-02-28_at_5.37.06_PM-removebg-preview.png')}
                    style={{height:'90%',
                    width:190,
                    marginTop:15
                    }}
                    
                    ></ImageBackground>
                    
                </View>
                <Text style={{fontFamily:'YanoneKaffeesatz',fontSize:75,color:'#521956',paddingTop:40,fontWeight:""}}>EIMS</Text>
                
                <LinearGradient
                 colors={['#070a13', '#164047', '#070a13']}
                 style={styles.button}>
                  <TouchableOpacity style={{
                 color:'#003f5c',
                  borderRadius: 25,
                  height:50,
                 width:280,
                  alignItems:"center",
                 justifyContent:"center",
                   alignSelf:"center",
                  backgroundColor: 'transparent',}}
                   onPress={ this.OnSendingData.bind(this)}>
                 <Text style={{color:"#ffffff",fontFamily:"monospace"}}>Login</Text>
                 </TouchableOpacity>
                </LinearGradient>
                <View style={{alignItems:'center',justifyContent:'center',shadowRadius:6, backgroundColor:'#ececec',height:40, width:'12%',alignSelf:'center',marginRight:20}}>
                    <Text>Contact us</Text>
                </View>
                <View style={{alignItems:'center',justifyContent:'center',shadowRadius:6, backgroundColor:'#ececec',height:40, width:'12%',alignSelf:'center',marginRight:20}} >
                    <Text>Help me</Text>
                </View>
                
                


                
                
                
                </View>
            <View style={{
                
                width:'100%',
                height:'100%',
                backgroundColor:'#FA5075'
            }}><Text>HAMMAD</Text></View>
           </View>
        );

    }
}


const styles=StyleSheet.create({
    background: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        height: 350,
      },
      imageview:{
        width: 450,
        height:400,
        marginBottom:25,
        alignContent:'center',
        justifyContent:'center',
        alignItems:'center',
        alignSelf:'center'
      },
      button: {
        padding: 15,
        alignItems: 'center',
        justifyContent:'center',
        alignSelf:'center',
        borderRadius: 5,
        height:55,
        width:290,
        marginBottom:70,
      },
      inputViewA:{
        borderBottomWidth:2,
        borderBottomColor:'#FF595C',
        marginBottom:10,
        marginTop:30,
        alignSelf:'center',

  
        alignItems:'center',
        alignContent:'center',
        justifyContent:"space-evenly",
        backgroundColor: "#ffffff",
        borderRadius: 28,
        width: "15%",
        height: 55,
      },
      inputViewB:{
        borderBottomWidth:2,
        borderBottomColor:'#FF595C',
        marginTop:10,
        alignSelf:'center',
  
        alignItems:'center',
        alignContent:'center',
        justifyContent:"space-evenly",
        backgroundColor: "#ffffff",
        borderRadius: 28,
        width: "80%",
        height: 55,
      },
      TextInput:{
        alignContent:"center",
        justifyContent:"center",
        alignItems:"center",
        fontFamily:"monospace",
        width:"100%",
        paddingLeft:8,
        height:55
        
        
  
      },
      imageviewbackground:{
        height:1000,
        width:800,

      }

})
