import React, { useState } from 'react';
import { View, Text, ImageBackground, TextInput, TouchableOpacity, StyleSheet } from 'react-native';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // TODO: handle login logic here
  };

  return (
    <ImageBackground
      style={styles.backgroundImage}
      source={require('./images/311381381_982955666431550_812384766897590576_n.jpg')} // Replace with your university's background image file
    >
      <View style={styles.overlay}>

        <View style={styles.container}>
          <View style={styles.titleContainer}>
            <Text style={styles.title}>University LMS</Text>
          </View>
          <View style={styles.inputContainer}>
            <TextInput
              style={styles.input}
              placeholder="Email"
              onChangeText={(text) => setEmail(text)}
              value={email}
              autoCapitalize="none"
              keyboardType="email-address"
            />
            <TextInput
              style={styles.input}
              placeholder="Password"
              onChangeText={(text) => setPassword(text)}
              value={password}
              secureTextEntry
            />
          </View>
          <TouchableOpacity style={styles.loginButton} onPress={handleLogin}>
            <Text style={styles.loginButtonText}>Log in</Text>
          </TouchableOpacity>
        </View>
      </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  backgroundImage: {
    flex: 1,
  },
  overlay: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    alignItems: 'center',
    justifyContent: 'center',
  },
  navBar: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    width: '100%',
    height: 50,
    paddingHorizontal: 20,
  },
  navButton: {
    paddingHorizontal: 10,
  },
  navButtonText: {
    color: '#ffffff',
    fontWeight: 'bold',
  },
  container: {
    backgroundColor: '#ffffff',
    width: '30%',
    borderRadius: 10,
    padding: 20,
    alignItems: 'center',
    justifyContent: 'center',
  },
  titleContainer: {
    marginBottom: 30,
  },
  title: {
    fontSize: 30,
    fontWeight: 'bold',
    color: '#880606',
  },
  inputContainer: {
    marginBottom: 30,
  },
  input: {
    width: '100%',
    height: 50,
    borderWidth: 1,
    borderColor: '#6C0404',
    borderRadius: 10,
    paddingHorizontal: 20,
    marginBottom: 20,
  },
  loginButton: {
    backgroundColor: '#880606',
    paddingVertical: 10,
    borderRadius: 10,
width: '30%',
alignItems: 'center',
justifyContent: 'center',
},
loginButtonText: {
color: '#ffffff',
fontWeight: 'bold',
fontSize: 16,
},
});

export default Login;
