import React, { Component } from 'react';
import { ImageBackground, StyleSheet, Text, View } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';




export default class Login extends Component{


    constructor(props){
        super(props)
        this.state={
            email:'',
            password:''

        }
    }


    render(){
        return(
            <View style={{flex:1,
                backgroundColor:'#ffffff',  
               alignItems: 'center',
                justifyContent: 'center',
                alignContent:"center" }}>
                   <LinearGradient
                colors={['rgba(0.8,0.2,0.2,0.3)', 'transparent']}
                style={styles.background}
              />
               <ImageBackground source={require('./images/lms-learning-management-system-vector_116137-2872-removebg.png')}style={styles.imageview}
        ></ImageBackground>
                <Text style={{fontSize:32,alignContent:'center',alignSelf:'center',alignItems:'center',fontWeight:'bold'}}>Welcome to Login Screen</Text>
            </View>
        );

    }
}


const styles=StyleSheet.create({
    background: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        height: 350,
      },
      imageview:{
        width: 200,
        height:180,
        justifyContent:"center",
        alignContent:"center",
        alignItems:"center",
        marginTop:40
      }
})
